export async function generateMetadata({ params }) {
  const { slug } = await params;
  const blog = blogsData[slug];

  if (!blog) return {};

  return {
    title: blog.metaTitle || blog.title,
    description: blog.metaDescription || blog.intro?.substring(0, 160),
    alternates: {
      canonical: blog.canonical,
    },
    openGraph: {
      title: blog.metaTitle || blog.title,
      description: blog.metaDescription || blog.intro?.substring(0, 160),
      url: blog.canonical,
      images: [{ url: blog.heroImage }],
      type: "article",
    },
  };
}

